# summarize accuracy for each Allele_frequency range and GP
perl -ne 'BEGIN{$freq_step=0.05}next if /^Fre/; chomp; @t=split /\s+/,$_; $ind=int($t[0]/$freq_step);  $h{$ind}{int($t[1]*10)/10}->[$t[-1]]++; END{ print join("\t", qw(Allele_freq GP_cutoff Wrong Correct Accuracy)), "\n"; @ids=sort {$a <=> $b}keys %h;  foreach $i (0..$#ids){ $ind=$ids[$i];  $s=join("", "[", $ind*$freq_step, ",", ($ind+1)*$freq_step, ")"); @gps=sort{$a <=> $b}keys %{$h{$ind}};  map{$gp=$_; ($zero, $one) = @{$h{$ind}{$gp}};  foreach $g(@gps){if($g>$gp){$zero+=$h{$ind}{$g}->[0]; $one+=$h{$ind}{$g}->[1];}}   print join("\t", $s, $gp, $zero, $one, $one/($zero+$one)),"\n" } @gps; } }'   $1 
