perl -ne 'BEGIN{$f=$ARGV[0]}@t=split /\t/, $_; $h{$t[0]}=1; if(/chloroplast/i){$ch{$t[0]}=1} if(/mitochon/i){$mt{$t[0]}=1}END{$ch_rate = (scalar keys %ch)/(scalar keys %h); $mt_rate=(scalar keys %mt)/(scalar keys %h);  print $f, "\t", "chloroplast:", sprintf("%.03f", $ch_rate), "\t", "mitochondria:", sprintf("%.03f", $mt_rate), "\t", "chloroplast+mitochondria:", sprintf("%.02f", $ch_rate+$mt_rate), "\n" }' $1 >${1}_mt_ch.txt
