perl -ne 'BEGIN{$f=$ARGV[0]}chomp;  @t=split /\s+/,$_; next if exists $h{$t[0]}; $spe=$1 if /^\S+\s+(\S+\s+\S+)/; $spe=$1 if /PREDICTED:\s+(\S+\s+\S+)/; $h{$t[0]}=1; $g{$spe}+= 1;END{ $tot=0; map{$tot += $_}values %g; @arr=sort{$g{$b} <=> $g{$a}}keys %g; @ps=map{$_. "_" . sprintf("%.2f", $g{$_}/$tot)}@arr[0..2];    print $f, "\t", join(";", @ps), "\n"; map{print STDERR $_, "\t", $g{$_}, "\n"}@arr[0..2]}' $1 >${1}.top3
